const Stack = createStackNavigator()

function EditScreen({navigation}){
    console.log(route.params)
    date = route.params[0]
    return (
        <View>
            <Text>Edit Screen.</Text>
            <Button onPress={()=>navigation.navigate("EditScreen",{date})} title="Confirm"/>
        </View>
    )
}

function DateScreen({navigation}){ //shows tasks
    console.log(route.params)
    //date = route.params[0]
    date = new Date()
    return (
        <View style={styles.container}>
            <Text>{`Today is the ${date.getDate()} of ${date.getMonth()}.`}</Text>
            <Button onPress={()=>navigation.navigate("EditScreen",{date})} title="Edit Tasks"/>
        </View>        
    )
}

function CalendarHome({navigation}){
    const Navigation = useNavigation()
    let d = new Date()
    let firstDay = new Date(d.getFullYear(),d.getMonth(),1)
    firstDay = firstDay.getDay() - 6 //show one week before
    //defining headers
    let days = ["Sun","Mon","Tue","Wed","Thu","Fri","Sat"]
    let dates = [
        [<View style={styles.row}>
            {days.map((day)=>
            <View style={styles.button}>
                <Text>{day}</Text>
            </View>)}
        </View>]
    ];
    console.log(dates)
    let temp;
    for (let row=0; row<6; row++){ //adding date
        temp = []
        for (let column=0; column<7; column++){
            let day = firstDay+column+7*row
            let date = new Date(d.getFullYear(),d.getMonth(),day)
            temp.push(<TouchableOpacity 
            style={styles.button} onPress={()=>navigation.navigate("DateScreen",{date})}>
                <Text style={()=>{
                    if (date.getDate()===d.getDate() && date.getMonth()===d.getMonth()){//same day
                        return {color:"red"}
                    } else if (date.getMonth()===d.getMonth()){ //same month
                        return {color:"black"}
                    } else {
                        return {color:"light-gray"} //different month
                    }
                }
                    }>
                    {date.getDate()}
                </Text>
            </TouchableOpacity>)
        }
        dates.push(<View style={styles.row}>{temp}</View>)
    }
    return (
        <View style={styles.container}>
            <View style={styles.header}>
                <TouchableOpacity><AntDesign name="left" size={24} color="black"/></TouchableOpacity>
                <Text>{["January","February","March","April","May","June","July","August","September","October","November","December"][d.getMonth()]} {d.getFullYear()}</Text>
                <TouchableOpacity><AntDesign name="right" size={24} color="black"/></TouchableOpacity>
            </View>
            {dates}
        </View>
    )
}
export default function CalendarStack(){
    return (
        <Stack.Navigator>
            <Stack.Screen name="CalendarHome" component={CalendarHome} options={{headerShown:false}}/>
            <Stack.Screen name="DateScreen" component={DateScreen}/>
            <Stack.Screen name="EditScreen" component={EditScreen}/>
        </Stack.Navigator>
    )
}

const styles = StyleSheet.create({
    container: {
        alignSelf:"center",
        flex:1,
        maxHeight:"40%",
        padding:10,
        
    },
    row: {
        flexDirection:'row',
        justifyContent:'space-between',
    },
    button: {
        alignItems:"center",
        justifyContent:"center",
        flex:1,
        padding:15,
        borderColor:'light-grey',
        borderWidth:1,
        aspectRatio:1,
    },
});
