import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View } from 'react-native';
import counterText from "./components/counterText.js";

export default function App() {
  console.log(counterText)
  let [count, setCount] = useState(0);
  function pressed() {
    setCount(count + 1);
  }
  function renderText(){
    if (count>=20){
      return ("20 keep going")
    } else if (count>=10){
      return ("10 lets go???")
    }
  }
  return (
    <View style={styles.container} onPress={pressed}>
      <counterText color="lightgrey" fontSize={10}>{count}</counterText>
      <counterText color="lightblue" fontSize={30}>{count}</counterText>
      <counterText color="blue" fontSize={60}>{count}</counterText>
      <counterText color="navy" fontSize={90}>{count}</counterText>
      <TouchableOpacity style = {styles.button} title="BUTTON" onPress={pressed}>
        <Text style = {styles.buttonText}>PRESS</Text>
      </TouchableOpacity>
      <Text>{renderText()}</Text>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  button: {
    backgroundColor:"red",
    padding:20,
    borderRadius:15,
    marginTop:20,
  },
  buttonText: {
    fontSize:60,
    fontWeight:"bold",
    color:"white",
  }
});
